import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ApiService } from './api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit{
  @ViewChild('f') submitForm!: NgForm;
  displayedData=''
  title="";

  constructor(private service : ApiService){}
  msg = '';
  // subForm !: FormGroup;

  ngOnInit(): void {
    // this.retrieve();
  }
  part1= ''
  part2= ''
  part3= ''


  showBufferAnimation: boolean = false;

  startBufferAnimation() {
    this.showBufferAnimation = true;
    setTimeout(() => {
      this.showBufferAnimation = false;
    }, 15000);
  }

  onClick(){
    // console.log(this.submitForm);
    // console.log(this.submitForm['value']);
    console.log('started===')
    this.service.send(this.submitForm['value']).subscribe(
      data =>{
        console.log("respose received");
        this.displayedData = data['text'];
        // this.router.navigate(['/'])
        const parts = this.displayedData.split("$$$");

        this.part1 = parts[1];
        this.part2 = parts[0].trimEnd();

        const bulk = this.part1.split("***");
        this.part1 = bulk[0];
        this.part3 = bulk[1];
        // console.log(this.part1);
        // console.log(this.part2);
      },
      error => {
        console.log(error);
      }
    )
  } 
