import { Component } from '@angular/core';

@Component({
    selector: 'app-email-button',
    template: `
    <a href="mailto:amitkumar.n@prodapt.com?subject=Hi, If you get this mail, it is working" (click)="handleEmailLinkClick($event)">Open Email</a>
  `
})
export class EmailButtonComponent {
    getEmailLink(): string {
        const emailAddress = 'amitkumar.n@prodapt.com';
        const subject = 'Hi there';
        const body = 'Mail works';

        return `mailto:${emailAddress}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    }
    handleEmailLinkClick(event: Event) {
        event.preventDefault();
        // Additional logic if needed
    }
}
