import { Component, OnInit } from '@angular/core';
import { Fields } from './fields.model';
import { FormBuilder, FormGroup } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ApiService } from './api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit{

  constructor(private formBuilder: FormBuilder, private http: HttpClient, private router:Router, private service : ApiService){}
  fields = new Fields();
  msg = '';
  submitForm !: FormGroup;

  ngOnInit(): void {
    this.submitForm = this.formBuilder.group({
      tcase : [''],
      lang: [''],
      query: ['']
    })
  }


  onClick(){
    this.service.onClick(this.fields).subscribe(
      data =>{
        console.log("respose received");
      },
      error => {
        console.log("exception occured");
        this.msg=error.error;
      }
    )
  } 
}
