import { Component } from '@angular/core';

@Component({
    selector: 'app-email-button',
    template: `
    <form [attr.action]="'mailto:amitkumar.n@prodapt.com'" method="post" enctype="text/plain">
    <input type="hidden" name="subject" [value]="'Your email subject'">
    <input type="hidden" name="body" [value]="'Your email body content'">
    <button type="submit">Open Email</button>
    </form>
    <a [href]="getEmailLink()" (click)="handleEmailLinkClick($event)">Open Email</a>
  `
})
export class EmailButtonComponent {
    getEmailLink(): string {
        const emailAddress = 'amitkumar.n@prodapt.com';
        const subject = 'Hi there';
        const body = 'Mail works';

        return `mailto:${emailAddress}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    }
    handleEmailLinkClick(event: Event) {
        event.preventDefault();
        // Additional logic if needed
    }
}
