import { Component, OnInit, ViewChild } from '@angular/core';
import { Fields } from './fields.model';
import { FormBuilder, FormGroup, NgForm } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { ApiService } from './api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit{
  @ViewChild('f') submitForm!: NgForm;
  ans=''
  title="";

  constructor(private formBuilder: FormBuilder, private http: HttpClient, private router:Router, private service : ApiService){}
  msg = '';
  // submitForm !: FormGroup;

  ngOnInit(): void {
    // this.retrieve();
  }
  // fields = new Fields();


  onClick(){
    // console.log(this.submitForm);
    // console.log(this.submitForm['value']);
    this.service.send(this.submitForm['value']).subscribe(
      data =>{
        console.log("respose received");
        // this.router.navigate(['/'])
      },
      error => {
        console.log("exception occured");
        this.msg=error.error;
      }
    )
  } 

  retrieve(): void {
    this.service.receive()
      .subscribe({
        next: (data) => {
          this.ans = data;
          console.log(data);
        },
        error: (e) => console.error(e)
      });
  }
}
